import React, { Component } from 'react';
import {BrowserRouter,Link,Route} from 'react-router-dom';

function Home(){
    return (
        <div>
        <h3>课程列表</h3>
        <ul>
            <li>
                <Link to="/detail/web">web架构师</Link>
            </li>
            <li>
                <Link to="/detail/python">python架构师</Link>
            </li>
        </ul>
    </div>
    )
}
function About(){
    return <div>About</div>
}
//传递进来的路由器对象
function Detail(props){
    console.log(props);

return <div>当前课程：{props.match.params.course}</div>
}
export default class RouteSample extends Component {
    render() {
        return (
            <div>
                {/* 父容器放在最外层 */}
               <BrowserRouter>
               <div>
                   {/* 导航的链接 */}
                   <div>
                   <ul>
                       <li>
                           <Link to="/">首页</Link>
                           <Link to="/about">关于</Link>
                       </li>
                       <li></li>
                   </ul>
                   </div>
                   {/* 路由的配置 ：路由即组件*/}
                   {/* 如果要单独显示，不匹配可以加exact */}
                   <Route  path="/" component={Home} />
                   <Route path="/detail/:course" component={Detail} />
                   <Route path="/about" component={About} />
               </div>
               </BrowserRouter>
            </div>
        )
    }
}
